package DSA_19_11_2022;

public class Reversethecharacterintriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       for(char i='E';i >='A';i--) {
    	   for(char j=i;j>='A';j--) {
    		   
    		   System.out.print(j+" ");
    	   }
    	   System.out.println();
       }
	}

}
