package DSA_19_11_2022;

public class printthealphabat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       for(char c='E';c>='A';c--) {
    	   for(char c1='A';c1<=c;c1++) {
    		   System.out.print(c1);
    	   }
    	   System.out.println();
    	   
       }
	}

}
